// ===== update_device_cache =====

// Raw reading from LoRa / previous function
let d = msg.payload || {};
const now = Date.now();

// keep a copy of the original one-device payload (for debugging)
msg.current = d;

// choose how long devices stay without updates before we drop them
const STALE_MS = 5 * 60 * 1000; // 5 minutes (change as you like)

// determine device name
let name = (d.deviceId || d.device || "").trim();

// load existing cache from flow context
let devices = flow.get("devices") || {};

// 1) clean cache: remove "unknown" / empty ids and very old devices
Object.keys(devices).forEach(id => {
    const dev = devices[id];

    // remove empty / "unknown" ids
    if (!id || id.toLowerCase() === "unknown") {
        delete devices[id];
        return;
    }

    // remove stale devices (no update for STALE_MS)
    if (dev && dev.lastSeen && (now - dev.lastSeen) > STALE_MS) {
        delete devices[id];
        return;
    }
});

// 2) if this message has no valid name -> ignore but keep cleaned cache
if (!name || name.toLowerCase() === "unknown") {
    flow.set("devices", devices);
    return null;
}

// 3) update / add this device
devices[name] = {
    ...(devices[name] || {}), // keep previous fields
    ...d,                     // overwrite with new data
    deviceId: name,           // normalized deviceId
    lastSeen: now
};

// 4) save back to context
flow.set("devices", devices);

// 5) output:
//    - payload: array of all devices (for UI)
//    - current: single reading (already set)
msg.payload = Object.values(devices);

return msg;

const p = msg.payload || {};

if (p.action === "delete" && p.deviceId) {
    let devices = flow.get("devices") || {};

    // remove this device from cache
    delete devices[p.deviceId];

    flow.set("devices", devices);

    // send updated list back to dashboard
    msg.payload = Object.values(devices);
    return msg;
}

// ignore anything else
return null;

let text;
try {
    text = msg.payload.toString('utf8'); // decode bytes
} catch (e) {
    text = JSON.stringify(msg.payload);  // fallback like Python repr()
}
msg.payload = text;
return msg;

let raw = (typeof msg.payload === "string") ? msg.payload.trim() : String(msg.payload);
let data = { raw };   // raw string altijd bewaren

try {
    const obj = JSON.parse(raw);
    Object.assign(data, obj);  // alle velden van JSON erbij
} catch (e) {
    node.warn("JSON parse failed, keeping raw only: " + e);
}

// Mooie tekst voor locatie
const device = data.device !== undefined ? String(data.device) : "Unknown";
const lat    = data.lat    !== undefined ? String(data.lat)    : "??";
const lon    = data.lon    !== undefined ? String(data.lon)    : "??";
const alt    = data.alt    !== undefined ? String(data.alt)    : "??";
const sats   = data.sats   !== undefined ? String(data.sats)   : "??";

data.location_str =
  "Device: " + device +
  " | Lat: " + lat + ", Lon: " + lon +
  " | Alt: " + alt + " m | Sats: " + sats;

// tempC als nummer + string
if (typeof data.tempC === "number" && !isNaN(data.tempC)) {
    data.tempC_str = data.tempC.toFixed(2);
}

msg.payload = data;
return msg;

const devices = Array.isArray(msg.payload) ? msg.payload : (flow.get('devices') ? Object.values(flow.get('devices')) : []);
const cols = flow.get('lcd_columns') || 16;    // set to 16 or 20 in flow context if needed
const rows = flow.get('lcd_rows') || 2;        // set to 2 or 4 in flow context if needed
const rotateMs = flow.get('lcd_rotate_ms') || 4000;

function padOrTrim(s, len) {
    s = String(s ?? '');
    if (s.length > len) return s.slice(0, len);
    return s + ' '.repeat(len - s.length);
}

// Build compact "screens" where each screen uses rows lines (commonly 2 lines for 16x2)
let devicesPerScreen = 1;
if (cols >= 20 && rows >= 4) devicesPerScreen = 2; // 20x4 -> two devices per screen
if (cols >= 20 && rows === 2) devicesPerScreen = 1;

const items = devices.map(d => {
    const id = d.deviceId || d.device || 'Unnamed';
    const temp = Number.isFinite(d.tempC) ? d.tempC.toFixed(1) + ' Graden C' : (d.tempC_str || '‚Äî');
    const batt = Number.isFinite(d.battery) ? Math.round(d.battery) + '%' : '';
    const sats = Number.isFinite(d.sats) ? String(d.sats) + 'sat' : '';
    return { id, temp, batt, sats };
});

// build screens (each screen is an array of 'rows' lines)
const screens = [];
for (let i = 0; i < items.length; i += devicesPerScreen) {
    const chunk = items.slice(i, i + devicesPerScreen);
    const lines = [];
    chunk.forEach(item => {
        // line 1 = device id
        lines.push(padOrTrim(item.id, cols));
        // line 2 = temp + maybe battery
        let right = item.temp;
        if (item.batt) right += ' ' + item.batt;
        if (right.length > cols && item.sats) {
            // try temp + sats if batt made it too long
            const alt = item.temp + ' ' + item.sats;
            if (alt.length <= cols) right = alt;
        }
        // final trim/pad
        lines.push(padOrTrim(right, cols));
        // if 4-row display add sats and batt as extra lines
        if (rows >= 4) {
            lines.push(padOrTrim(item.sats || '', cols));
            lines.push(padOrTrim(item.batt || '', cols));
        }
    });
    // pad to rows
    while (lines.length < rows) lines.push(' '.repeat(cols));
    screens.push(lines.slice(0, rows));
}

// If no devices, show placeholder
if (screens.length === 0) {
    screens.push([padOrTrim('No devices', cols), padOrTrim('', cols)]);
}

// rotation handling (store index/time in flow context)
let idx = flow.get('lcd_screen_index') || 0;
const last = flow.get('lcd_last_rotate') || 0;
const now = Date.now();
if (now - last >= rotateMs) {
    idx = (idx + 1) % screens.length;
    flow.set('lcd_screen_index', idx);
    flow.set('lcd_last_rotate', now);
}
// allow external trigger (if caller sets msg.forceNext === true) to advance immediately
if (msg.forceNext === true) {
    idx = (idx + 1) % screens.length;
    flow.set('lcd_screen_index', idx);
    flow.set('lcd_last_rotate', now);
}

const screen = screens[idx];

// Convert to the object-array format your LCD node accepts (matches your test msg)
const out = screen.map(line => {
    return { clear: true, text: line, alignment: "left" };
});

// Attach some debug info for troubleshooting
msg._lcd_debug = {
    index: idx,
    totalScreens: screens.length,
    cols, rows
};

msg.payload = out;
return msg;

if(msg.payload == 0){
    flow.set("power", 0);// turn off device
}
msg.payload = flow.get("power");
return msg;

